using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.Numerics;

namespace _03.Bullz_and_Cowz
{
    class Program
    {
        static void Main(string[] args)
        {
           string sGivenInteger = Console.ReadLine();
           string uGivenInteger = sGivenInteger.Replace("-", "");
            //to string
           string givenIntegerStrinified = uGivenInteger;
            //get special sum.
            //if number is in odd position => sum += number * position^2
            //if number is in even position => sum += value^2*position
            
            int sum = 0;
            int temp = 0;
            int position = 1;
           
                for (int i = givenIntegerStrinified.Length; i > 0; i--)
                {
                    // 111
                    // i=3 ; position = 1; -> vliza v purvi test.
                    //temp = GIS[2] = 1 ot posledna poziciq.
                    //0=0+(1*1*1); sum=1;
                    // i=2; position=2; => vliza v vtori test 
                    // temp = GIS[1] 
                    //sum = 1+(1*2)=3
                    //i=1; iposition = 3; => vliza v puriv test.
                    //temp=GIS[0]
                    //sum= 1*3*3 = 9;
                    if (position % 2 == 0)
                    {
                        temp = int.Parse(givenIntegerStrinified[i - 1].ToString());
                        sum = sum + (temp * temp * position);
                    }
                    if(position%2  ==1)
                    {
                        temp = int.Parse(givenIntegerStrinified[i - 1].ToString());
                        sum = sum + (temp * position * position);
                    }
                    position++;
                }
            int specialnum = sum % 10;
            //Length of alpha sequence = specialnum. 48-26 = 22
            if (specialnum == 0)
            {
                Console.WriteLine(sum);
                Console.WriteLine("{0} has no secret alpha-sequence", sGivenInteger);
            }
            else
            {
                int R = sum % 26;
                string EnglishAlphabet2x = "";
                char[] EnglishAlphaSequenceBet = EnglishAlphabet2x.ToCharArray();
                Console.WriteLine(sum);
                for (int i = 0; i < specialnum; i++)
                {
                    Console.Write(EnglishAlphaSequenceBet[R + i]);
                }
            }
        }
    }
}
